from __future__ import absolute_import
from __future__ import division
from __future__ import print_function

import os
import errno

__all__ = ['mkdir_p']


def mkdir_p(path):
  try:
    os.makedirs(path)
  except OSError as exc:  # Python >2.5
    if exc.errno == errno.EEXIST and os.path.isdir(path):
      pass
    else:
      raise
