import tensorflow as tf
import numpy as np

class MultiHeadAttention(tf.keras.layers.Layer):

    def __init__(self, d_model, num_heads):
        """
        :param d_model: The Dimensionality of the Attention mechanism
        :param num_heads: The number of heads to use in the attention mechanism
        """
        super(MultiHeadAttention, self).__init__()
        self.num_heads = num_heads
        self.d_model = d_model

        assert d_model % self.num_heads == 0

        self.depth = d_model // self.num_heads

        self.wq = tf.keras.layers.Dense(d_model)
        self.wk = tf.keras.layers.Dense(d_model)
        self.wv = tf.keras.layers.Dense(d_model)

        self.dense = tf.keras.layers.Dense(d_model)

    @staticmethod
    def scaled_dot_product_attention(q, k, v, mask):
        """
        :param q: The query vector
        :param k: The key vector
        :param v: The value vector
        :param mask: Float tensor with shape (..., seq_len_q, seq_len_k). Defaults to None.
        :return: the scaled dot product output and attention weights (output, attention_weights)
        """

        matmul_qk = tf.matmul(q, k, transpose_b=True)  # (..., seq_len_q, seq_len_k)

        # scale matmul_qk
        dk = tf.cast(tf.shape(k)[-1], tf.float32)
        scaled_attention_logits = matmul_qk / tf.math.sqrt(dk)

        # add the mask to the scaled tensor.
        if mask is not None:
            scaled_attention_logits += (mask * -1e9)

        # softmax is normalized on the last axis (seq_len_k) so that the scores
        # add up to 1.
        attention_weights = tf.nn.softmax(scaled_attention_logits, axis=-1)  # (..., seq_len_q, seq_len_k)

        output = tf.matmul(attention_weights, v)  # (..., seq_len_q, depth_v)

        return output, attention_weights

    def split_heads(self, x, batch_size):
        """
        :param x: the head input
        :param batch_size:
        :return: splits the last dimension and returns the result with shape (batch_size, num_heads, seq_length, depth)
        """
        x = tf.reshape(x, (batch_size, -1, self.num_heads, self.depth))
        return tf.transpose(x, perm=[0, 2, 1, 3])

    def call(self, v, k, q, mask):
        """
        :param v: Value vector
        :param k: Key vector
        :param q: Query vector
        :param mask: Mask shape to use
        :return:
        """
        batch_size = tf.shape(q)[0]

        q = self.wq(q)  # (batch_size, seq_len, d_model)
        k = self.wk(k)  # (batch_size, seq_len, d_model)
        v = self.wv(v)  # (batch_size, seq_len, d_model)

        q = self.split_heads(q, batch_size)  # (batch_size, num_heads, seq_len_q, depth)
        k = self.split_heads(k, batch_size)  # (batch_size, num_heads, seq_len_k, depth)
        v = self.split_heads(v, batch_size)  # (batch_size, num_heads, seq_len_v, depth)

        # scaled_attention.shape == (batch_size, num_heads, seq_len_q, depth)
        # attention_weights.shape == (batch_size, num_heads, seq_len_q, seq_len_k)
        scaled_attention, attention_weights = self.scaled_dot_product_attention(
            q, k, v, mask)

        scaled_attention = tf.transpose(scaled_attention,
                                        perm=[0, 2, 1, 3])  # (batch_size, seq_len_q, num_heads, depth)

        concat_attention = tf.reshape(scaled_attention,
                                      (batch_size, -1, self.d_model))  # (batch_size, seq_len_q, d_model)

        output = self.dense(concat_attention)  # (batch_size, seq_len_q, d_model)

        return output, attention_weights

    @staticmethod
    def point_wise_feed_forward_network(d_model, dff, activation='gelu'):

        if activation == "gelu":
            activation = tf.keras.layers.Activation(gelu)
        elif activation == "swish":
            activation = tf.keras.layers.Activation(swish)
        elif activation == "swish_beta":
            activation = tf.keras.layers.Activation(swish_beta)
        elif activation == "mish":
            activation = tf.keras.layers.Activation(mish)
        else:
            activation = activation

        return tf.keras.Sequential([
            tf.keras.layers.Dense(dff, activation=activation),  # (batch_size, seq_len, dff)
            tf.keras.layers.Dense(d_model)  # (batch_size, seq_len, d_model)
        ])



def gelu(x):
  cdf = 0.5 * (1.0 + tf.tanh((np.sqrt(2 / np.pi) * (x + 0.044715 * tf.pow(x, 3)))))
  return x * cdf


#swish activation function 추가
def swish(x):
    return x * tf.nn.sigmoid(x)

#swish-beta activation function 추가
def swish_beta(x):
    beta=tf.Variable(initial_value=1.0,trainable=True, name='swish_beta')
    return x * tf.nn.sigmoid(beta * x) #trainable parameter beta

#mish activation function 추가
def mish(x):
    return x * tf.math.tanh(tf.math.softplus(x))
